sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var obj = new GlideAjax('BR_util');
    obj.addParam('sysparm_name', 'getUseremail');
    obj.addParam('sysparm_user', newValue);
    obj.getXML(getResponse);

    function getResponse(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var data = answer.split(',');

        g_form.setValue('short_description' + data);
        g_form.setValue('email', data[0]);
        g_form.setValue('department', data[1]);
		if(data[2].toString().length()>=10)
			{
        g_form.setValue('mobile_no', data[2]);
			}
        alert(data);
    }
}
